@dec
def puzir(array):
    n=0
    while len(array)-1 != n:
        for i in range(1,len(array)-n):
            if array[i-1] > array[i]:
                # last = array[i-1]
                # array[i-1] = array[i]
                # array[i] = last   
                array[i - 1], array[i] = array[i], array[i - 1] #удобная конструкция множественного присвоения вместо закомменченного сверху
        n+=1
    return array

